package com.lxl.jianzhiOffer.reOrderArray;/** * @author lixiaole * @date 2018/9/25 *//** * 调整数组顺序使奇数位于偶数前面 */public class Solution {    public void reOrderArray(int [] array) {        if(array == null || array.length <=0){            return;        }        int start = 0 ;        int count = 0;        int[] newarray = new int[array.length];        for(int i = 0 ;i<array.length ;i++){            if((array[i]&1) ==1){                count++;            }        }        for(int i = 0; i<array.length ;i++){            if((array[i]&1) ==1){                newarray[start++] = array[i];            }else{                newarray[count++] = array[i];            }        }        for(int i = 0; i<array.length ;i++){            array[i] = newarray[i];        }    }}